
/**
 * Module dependencies.
 */

var connect = require('../');

var account = connect(function(req, res){
  var location = 'http://kiwiz.herokuapp.com:3000/account/' + req.headers.host.split('.kiwiz.herokuapp.com')[0];
  res.statusCode = 302;
  res.setHeader('Location', location);
  res.end('Moved to ' + location);
});

var blog = connect(function(req, res){
  res.end('blog app');
});

var main = connect(function(req, res){
  if (req.url == '/') return res.end('main app');
  if (0 == req.url.indexOf('/account/')) return res.end('viewing user account for ' + req.url.substring(9));
  res.statusCode = 404;
  res.end();
});

connect(
    connect.logger()
  , connect.vhost('blog.kiwiz.herokuapp.com', blog)
  , connect.vhost('*.kiwiz.herokuapp.com', account)
  , main
).listen(3000);
